    const query = `
      INSERT INTO survey_results 
      (survey_assignment_id, parameter_name, raw_score, standardized_score, interpretation_text, indicator)
      VALUES ${results.map((_, i) => `($${i*6+1}, $${i*6+2}, $${i*6+3}, $${i*6+4}, $${i*6+5}, $${i*6+6})`).join(', ')}
